import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";
import "../styles/Cart.css";

function Cart() {
  const { cartItems, removeFromCart, clearCart, totalPrice } =
    useContext(CartContext);

  return (
    <div className="cartPage">
      <h1>Your Cart ðŸ›’</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty. Add some pizzas!</p>
      ) : (
        <>
          {cartItems.map((item) => (
            <div key={item.name} className="cartItem">
              <img src={item.image} alt={item.name} />
              <div>
                <h3>{item.name}</h3>
                <p>
                  â‚¹{item.price} Ã— {item.quantity}
                </p>
                <button onClick={() => removeFromCart(item.name)}>
                  Remove
                </button>
              </div>
            </div>
          ))}
          <h2>Total: â‚¹{totalPrice.toFixed(2)}</h2>
          <button className="clearBtn" onClick={clearCart}>
            Clear Cart
          </button>
        </>
      )}
    </div>
  );
}

export default Cart;
