import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";
import "../styles/Cart.css";

function Cart() {
  const { cartItems, addToCart, decreaseQuantity, removeFromCart, clearCart, totalPrice } =
    useContext(CartContext);

  return (
    <div className="cartPage">
      <h1>Your Cart ðŸ›’</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty. Add some pizzas!</p>
      ) : (
        <>
          {cartItems.map((item) => (
            <div key={item.name} className="cartItem">
              <img src={item.image} alt={item.name} />
              <div className="cartDetails">
                <h3>{item.name}</h3>
                <p>â‚¹{item.price.toFixed(2)}</p>
                <div className="quantityControls">
                  <button onClick={() => decreaseQuantity(item.name)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => addToCart(item)}>+</button>
                </div>
                <p className="subtotal">
                  Subtotal: â‚¹{(item.price * item.quantity).toFixed(2)}
                </p>
                <button className="removeBtn" onClick={() => removeFromCart(item.name)}>
                  Remove
                </button>
              </div>
            </div>
          ))}
          <div className="cartSummary">
            <h2>Total: â‚¹{totalPrice.toFixed(2)}</h2>
            <button className="clearBtn" onClick={clearCart}>Clear Cart</button>
          </div>
        </>
      )}
    </div>
  );
}

export default Cart;
